import React, { useState } from 'react'
import { useSongs } from './util/hooks'
import { mutate } from 'swr'

const CreateItem = () => {
  const [input, setInput] = useState("")
  const [madeBy, setMadeB] = useState("")
  const { songs } = useSongs()

  return (
    <form
      onSubmit = {async e => {
          e.preventDefault()

          mutate("/api/songs", [{name: name, madeBy: madeBy}, ...songs], false)
          setInput("")
      }}
    >
      <input value = {name} onChange = { e => setInput(e.target.value)} />
      <input value = {madeBy} onChange = { e => setInput(e.target.value)} />
      <input type="submit" value="add"/>
    </form>
  )
}

export default CreateItem
